import React from 'react';

export default class OrderMsg extends React.Component {
state = {}
render(){

return(
    <div style={decor.small}>
    <p style={{...decor.register, ...decor.border}}>Shopping Cart</p>
    <div style={decor.borderItem}>
    {['product','description','color','size','units','amount','delete',].map((ele,i)=>(
        <p style={decor.descriptions}>{ele} </p>
    ))}
        </div>
    <div>
    {
        item1.map(ele=>{
            return( 
                <div style={decor.borderItem}>
                    <div style={decor.image}><img style={decor.pic}src={ele.image} alt='pic'/></div> 
                    <div style={decor.items}>{ele.description}</div>
                    <div style={decor.items}>{ele.color}</div>
                    <div style={decor.items}>{ele.size}</div>
                    <div style={decor.items}><i style={decor.icons} className='fas fa-minus'></i>&nbsp;&nbsp;&nbsp;<span style={decor.quantity}>&nbsp;1&nbsp;</span>&nbsp;&nbsp;&nbsp;<i style={decor.icons} className='fas fa-plus'></i></div>
                    <div style={decor.items}>{ele.price}</div>
                    <div style={decor.trash}><i class="fas fa-trash"></i></div>
                </div>
            )
        })
    }  
    </div>  
    <div style={decor.border2}></div>
    <div style={decor.total}>
      Total: {this.state.total} EUR
    </div>
    <div style={decor.grid}>
        <button className='buttons' style={decor.buttonLeft}>Continue Shopping</button>  
        <button className='buttons' style={decor.button}>Check Out</button>
    </div>  
    </div>
    )
}
}

const decor = {
    small: {
        width: '70%',
        margin: 'auto'
    },
    register: {
        fontSize: '30px',
        textTransform: 'uppercase',
        textAlign: 'center'
    },
    borderItem:{
        display: 'grid',
        gridTemplateColumns: '1.5fr 2fr 1fr 1fr 1fr 1fr 1fr',
        borderBottom: '1px solid rgb(242, 242, 242)',
    },    
    border: {
        borderBottom: '2px solid rgb(42, 43, 45)',
        paddingTop: '2em',
        paddingBottom: '2em'
    },
    image:{
        height:'15vh',
        position: 'relative',
        display: 'block',
        margin: '15px auto'
    },
    pic:{
        maxWidth: '100px',
        maxHeight: '100px',
    },
    trash:{
        fontSize:'15px',
        position: 'relative',
        display: 'block',
        margin: 'auto'
    },
    descriptions:{
        position: 'relative',
        display: 'block',
        margin: 'auto',
        fontSize: '15px',
        textTransform: 'uppercase',
        paddingTop: '2em',
        marginBottom: '1em'
    },
    items:{
        position: 'relative',
        display: 'block',
        margin: 'auto'
    },
    button:{
        backgroundColor: 'rgb(26, 27, 28)',
        color: 'white',
        fontSize: '15px',
        textTransform: 'uppercase',
        border: '2px solid rgb(26, 27, 28)',
        position: 'relative',
        textAlign: 'center',
        width: '220px',
        height: '40px',
        outline: 'none',
        marginTop: '2em',
        marginLeft: 'auto',
    },
    buttonLeft:{
        backgroundColor: 'rgb(26, 27, 28)',
        color: 'white',
        fontSize: '15px',
        textTransform: 'uppercase',
        border: '2px solid rgb(26, 27, 28)',
        position: 'relative',
        textAlign: 'center',
        width: '220px',
        height: '40px',
        outline: 'none',
        marginTop: '2em',
        marginRight: 'auto',
    },
    grid:{
        display: 'grid',
        gridTemplateColumns: '1fr 1fr'
    },
    border2:{
        borderBottom: '2px solid rgb(42, 43, 45)',
        paddingTop: '2em' 
    },
    icons:{
        fontSize: '12px'
    },
    quantity:{
        border: '1px solid grey',
        fontSize: '16px',
        padding: '4px'
    },
    total:{
        display: 'flex',
        justifyContent: 'flex-end',
        textTransform: 'uppercase',
        marginTop: '1em',
        fontSize: '19px',
        fontWeight: 'bold'
    }
}
