import React from 'react';
import {bags} from './Stock'
export default class Bag extends React.Component{
    render(){
         return(
        <div style={decor.small}>
            <div style={decor.grid}>
            <div>
                <img src={this.state.image} style={decor.img} alt='image'/>
            </div>
            <div style={decor.product}>
                <div>{bags[this.props.match.params.id].title}</div>
                <div>{bags[this.props.match.params.id].designer}</div>
                <div>{bags[this.props.match.params.id].price}</div>
                <div style={decor.border}>{bags[this.props.match.params.id].description}</div>
            </div>
        </div>
        </div>
         )
    }
}
const decor = {
    grid:{
        display:'grid',
        gridTemplateColumns: '1fr 1fr'
    },
    small:{
        width: '70%',
        marginLeft: '9em',
    },
    img:{
        width: '80%'
    },
    product:{
        paddingTop: '17em',
        fontFamily:"'EB Garamond', serif"
    },
    border:{
        borderTop: '1px solid black',
        borderBottom: '1px solid black',
        padding: '10px'
    }
}
