import React from 'react';

export default class ShoppingCart extends React.Component {
    state = {
       item1:{title: 'Drawstring Bag', designer:'Chanel', price: '€4000', description: 'Grained Calfskin & Gold-Tone Metal', category: 'bags', color: 'white', image: 'https://www.chanel.com/images/t_fashion/q_auto,f_jpg,fl_lossy,dpr_2/w_1240/drawstring-bag-white-grained-calfskin-gold-tone-metal-grained-calfskin-gold-tone-metal-packshot-default-as0310b0017010601-8812985843742.jpg', sale: false},
    }
    render(){
    return(
        <div style={decor.small}>
        <p style={{...decor.register, ...decor.border}}>Shopping Cart</p>


        </div>
        )
    }
}

const decor = {
    small: {
        width: '70%',
        margin: 'auto'
    },
    register: {
        fontSize: '30px',
        textTransform: 'uppercase',
        textAlign: 'center'
    },

    border: {
        borderBottom: '2px solid rgb(42, 43, 45)',
        paddingBottom: '1em',
        paddingTop: '1em'
    }
}
