import React from 'react';
import axios from 'axios'

export default class EmailFromUser extends React.Component {
  state = {message:'', subject:'', info:''}
  handleChange = e =>{
    this.setState({[e.target.name]:e.target.value},()=>{
      console.log(this.state)
    })
  }
  handleClick = async() =>{
    let url = 'http://localhost:8080/sendEmail'
    try{
      let { message, subject }= this.state
      const done = await axios.post(url, { subject, message })
      if(done.status === 200){
        this.setState({info:'Message sent!'})
      }else{
        this.setState({info:'something went wrong ...'})
      }
    }
    catch(error){
      debugger
    }
  }
  render() {
    return (
      <div>
        <h1>{this.state.info}</h1>
      <h2>Create your e-mail!</h2>
      <div style={decor.form}>
      <div style={decor.input}>
        <h3>Subject:</h3>
        <input
        onChange= {this.handleChange}
        placeholder = 'enter subject'
        name = "subject"
        value = {this.state.subject}
        />
        <h3>Message:</h3>
        <input
        onChange= {this.handleChange}
        placeholder = 'enter message'
        name = 'message'
        value = {this.state.message}
        />
      </div>
      <div style={decor.button}>
        <button
        onClick = {this.handleClick}
        >send</button>
      </div> 
      </div>
      </div>
    );
  }
}

const decor = {
  form:{
    margin: 'auto',
    color: 'black',
    display: 'grid',
    gridTemplateColumns: '1fr 1fr'
  },
  h3:{

  },
  input:{
    paddingTop: '200px',
    paddingLeft: '500px',
  },
  button:{
    paddingTop: '300px',
  }
}
