import React from 'react';
import {NavLink} from 'react-router-dom';



export default class Shoes extends React.Component{
    render(){

    return(
    <div style={decor.grid}>
    {
        this.props.stock.map((ele, i)=>{
            return( 
            <NavLink to={`/shoes/${ele._id}`}> 
            <div style={decor.small}>
                <img alt="shoe1" style={decor.img} src={ele.img}/>
                <p style={decor.name}>{ele.name}</p>
                <p style={decor.brand}>{ele.designer}</p>
                <p style={decor.price}>{ele.price} â‚¬</p>
            </div>
            </NavLink>   
            )
        })
    }    
    </div>
    )
    }
}
const decor = {
    grid:{
    display:'grid',
    gridTemplateColumns: '1fr 1fr 1fr',
    gridGap: '3em',
    width: '80%',
    margin: 'auto'
    },
    small:{
    marginTop:'4em'
    },
    img:{
        width: '80%',
        margin: 'auto'
    },
    name:{
    position: 'relative',
    textAlign:'center',
    },
    brand:{
    position: 'relative',
    textAlign:'center',
    },
    price:{
    position: 'relative',
    textAlign:'center',
    }
}
