import React from 'react';

export default class Admin extends React.Component{
    state={
        title:'', designer:'', price:'', description:'', category:'', color:'', img:'', sale:''
    }
    //sale should be set to false and then we change or empty?
    handleChange=e=>{
        this.setState({[e.target.name]: e.target.value})
    }
    handleSubmit=()=>{

    }
    render(){
        let {title, designer, price, description, category, color, img, sale}=this.state
        return(
        <div style={decor.big}>
            <div>
            <h2>Add new product</h2>
            <form  style={decor.box}
            onSubmit={this.handleSubmit}>
                <label style={decor.label}>Title</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter title'
                name="title"
                value={title}
                />  
                <label style={decor.label}>Description</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter description'
                name="description"
                value={description}
                />  
                <label style={decor.label}>Designer</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter designer'
                name="designer"
                value={designer}
                /> 

                <label style={decor.label}>Price</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter price'
                name="price"
                value={price}
                />   

                <label style={decor.label}>Color</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter color'
                name="color"
                value={color}
                /> 

                <label style={decor.label}>Upload Image</label>         
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='add img url'
                name="img"
                value={img}
                />  
                <label style={decor.label}>Status</label>                
                <select onChange = {this.handleChange} style={decor.select} value={sale}>
                    <option name='sale' value='true'>on sale</option>
                    <option name='sale' value='false'>regular price</option>
                </select>  
                <div>       
                <button style={decor.button}>
                Submit
                </button>  
                </div>    
            </form>
            </div>
        </div>
        ) 
    }
}

const decor={
    big:{
        marginTop: '3em',
        marginLeft: 'auto',
        marginRight: 'auto',
        paddingLeft: '2em',
        width: '60%',
        borderBotton:'solid 1px black',
        marginBottom:'2em'
    },
    box:{
        marginTop: '2em'
    },
    label:{
        marginBottom: '1em',
        marginTop: '1em',
        display: 'inline-block',
        float: 'left',
        clear: 'left',
        width: '250px',
        textAlign: 'left'
    },
    select:{
        width:'400px',
        border: '1px solid #cbcbcb',
        height: '30px',
        display: 'inline-block',
        float: 'left',
        padding: '6px 30px 6px 6px',
        backgroundPosition: 'right -272px',
        margin: '0',
        outline: 'none',
        background: 'white',
    },
    input:{
        width:'360px',
        display: 'inline-block',
        float: 'left',
        padding: '6px 30px 6px 6px',
        backgroundPosition: 'right -272px',
        cursor: 'pointer',
        border: '1px solid #cbcbcb',
        outline: 'none',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none'
    },
    button:{
        marginTop: '2em',
        marginBotton: '2em',
        width: '200px',
        display: 'grid',
        float: 'left',
        backgroundPosition: 'right -272px',
        outline: 'none'
    }
}
