import React from 'react';
import SoldOut from './SoldOut'

export default class ShoppingCart extends React.Component {
state = {
    total:'',
     title: "", 
     stock:'',
     price: "", 
     color:'', 
     img:'', 
     unit:'1',
     _id:'',
     shoppingCart:[],
     display:'none'
}

goShopping=()=>{
    this.props.history.push({pathname:"/products"})
}
checkOut=()=>{
    this.props.history.push({pathname:"/buy"})
}
componentDidMount(){
    var productStorage = JSON.parse(localStorage.getItem('shoppingCart'))
    this.setState({shoppingCart:productStorage})
    console.log( "this is the product storage", productStorage)
}
deleteFromCart =(key)=>{
    localStorage.removeItem(key);
    this.setState({shoppingCart:JSON.parse(localStorage.getItem('shoppingCart'))}) 
}
// increaseOne = (id)=>{
//     let {unit}= this.state
//     if (true /*stock*/){
//     unit++
//     this.setState({unit})
//     }
//     else{
//         this.setState({display:'block'})
//         setTimeout(()=>{
//             this.setState({display:'none'})   
//         },1000)
//     }
// }
minus = () => {
  if(this.state.items > 1) {
    this.setState({ items: this.state.items - 1 });
  } else {
    this.setState({items: 1})
  }
}
plus = () => {
  this.setState({ items: this.state.items + 1 });
}
removeOneInCart = ()=>{
    return true
}
addOneInCart = ()=>{
    return true
}
render(){
let { unit, shoppingCart, total} = this.state
return(
    <div style={decor.small}>
        <p style={{...decor.register, ...decor.border}}>Shopping Cart</p>
        <div style={decor.borderItem}>
        {
        ['product','description','color','units','amount','delete'].map((ele,i)=>(
            <p style={decor.descriptions}>{ele}</p>
        ))
        }
        </div>
        <div>
        {
        shoppingCart.map((ele,i)=>{
            return( 
                <div key={i} style={decor.borderItem}>
                <div style={decor.image}><img style={decor.pic}src={ele.img} alt='pic'/></div> 
                <div style={decor.items}>{ele.title}</div>
                <div style={decor.items}>{ele.color}</div>
                <div style={decor.items}>
                    <i onClick={this.minus} style={decor.icons} className='fas fa-minus'></i>
                        <span style={decor.quantity}>{unit}</span>
                    <i onClick={this.plus} style={decor.icons} className='fas fa-plus'></i>
                    <SoldOut
                    display={this.state.display}
                    />
                </div>
                    <div style={decor.items}>{ele.price}</div>
                    <div style={decor.trash}><i onClick={()=>this.deleteFromCart()} className="fas fa-trash"></i></div>
                </div>
            )
        })
        }  
        </div>  
        <div style={decor.border2}></div>
        <div style={decor.total}>Total: {total} EUR</div>
        <div style={decor.grid}>
            <button 
            onClick={this.goShopping}
            className='buttons' style={decor.buttonLeft}>Continue Shopping</button>  
            <button 
            onClick={this.checkOut}
            className='buttons' style={decor.button}>Check Out</button>
        </div>  
    </div>
    )
}
}

const decor = {
    small: {
        width: '70%',
        margin: 'auto'
    },
    register: {
        fontSize: '30px',
        textTransform: 'uppercase',
        textAlign: 'center'
    },
    borderItem:{
        display: 'grid',
        gridTemplateColumns: '1.5fr 2fr 1fr 1fr 1fr 1fr',
        borderBottom: '1px solid rgb(242, 242, 242)',
    },    
    border: {
        borderBottom: '2px solid rgb(42, 43, 45)',
        paddingTop: '2em',
        paddingBottom: '2em'
    },
    image:{
        height:'15vh',
        position: 'relative',
        display: 'block',
        margin: '15px auto'
    },
    pic:{
        maxWidth: '100px',
        maxHeight: '100px',
    },
    trash:{
        fontSize:'15px',
        position: 'relative',
        display: 'block',
        margin: 'auto'
    },
    descriptions:{
        position: 'relative',
        display: 'block',
        margin: 'auto',
        fontSize: '15px',
        textTransform: 'uppercase',
        paddingTop: '2em',
        marginBottom: '1em'
    },
    items:{
        position: 'relative',
        display: 'block',
        margin: 'auto'
    },
    button:{
        backgroundColor: 'rgb(26, 27, 28)',
        color: 'white',
        fontSize: '15px',
        textTransform: 'uppercase',
        border: '2px solid rgb(26, 27, 28)',
        position: 'relative',
        textAlign: 'center',
        width: '220px',
        height: '40px',
        outline: 'none',
        marginTop: '2em',
        marginLeft: 'auto',
    },
    buttonLeft:{
        backgroundColor: 'rgb(26, 27, 28)',
        color: 'white',
        fontSize: '15px',
        textTransform: 'uppercase',
        border: '2px solid rgb(26, 27, 28)',
        position: 'relative',
        textAlign: 'center',
        width: '220px',
        height: '40px',
        outline: 'none',
        marginTop: '2em',
        marginRight: 'auto',
    },
    grid:{
        display: 'grid',
        gridTemplateColumns: '1fr 1fr'
    },
    border2:{
        borderBottom: '2px solid rgb(42, 43, 45)',
        paddingTop: '2em' 
    },
    icons:{
        fontSize: '12px',
        margin: '10px'
    },
    quantity:{
        border: '1px solid grey',
        fontSize: '16px',
        padding: '4px'
    },
    total:{
        display: 'flex',
        justifyContent: 'flex-end',
        textTransform: 'uppercase',
        marginTop: '1em',
        fontSize: '19px',
        fontWeight: 'bold'
    }
}
