import React from 'react';
import axios from 'axios';

export default class Product extends React.Component{
    state={
        sale:'' , 
        _id: "",
        shoppingCart:[],

    }
    componentDidMount(){
        this.findProduct()
        let { shoppingCart } = this.state
        var productStorage = JSON.parse(localStorage.getItem('shoppingCart'))
        this.setState({shoppingCart:productStorage})
        console.log( "this is the shopping car ", shoppingCart)
    }
    findProduct = async () => {
        debugger
        let url = `http://localhost:3001/products/product/${this.props.match.params.id}`;
        try{
            const product = await axios.get(url)
            debugger
            this.setState({sale:product.data.myProduct.sale , _id:product.data.myProduct._id, title:product.data.myProduct.title, designer:product.data.myProduct.designer, price:product.data.myProduct.price, color:product.data.myProduct.color, img:product.data.myProduct.img, description:product.data.myProduct.description})
            console.log({product})
        }
        catch(error){
            debugger
            console.log({error})
        }
    }
    addToBag = async () => {
        let shoppingCart  = JSON.parse(localStorage.getItem('shoppingCart')) || [];
        let _id = this.props.match.params.id
        shoppingCart.push({_id:_id})
        localStorage.setItem('shoppingCart', JSON.stringify(shoppingCart))
        this.setState({ shoppingCart })
        console.log("this is the function addtobag of product single", shoppingCart)

     }
    render(){
        let { title, designer, price, img, description} = this.state;
        return(
        <div style={decor.small}>
            <div className='productGrid'>
            <div>
                <img src={img} style={decor.img} alt='productPic'/>
            </div>
            <div style={decor.product}>
                <div style={decor.title}>{title}</div>
                <div style={decor.designer}>{designer}</div>
                <div>{price}</div>
                <div style={decor.border}>{description}</div>
                <button onClick={this.addToBag} style={decor.button} className="buttons">Add</button>
            </div>
        </div>
        </div>
         )
    }
}
const decor = {
    grid:{
        display:'grid',
        gridTemplateColumns: '1fr 1fr'
    },
    small:{
        width: '70%',
        marginLeft: '9em',
    },
    img:{
        paddingTop: '2em',
        Height: '400px',
        width: '80%'
    },
    product:{
        maxHeight: '400px',
        width: '80%',
        paddingTop: '6em',
        fontFamily:"'EB Garamond', serif"
    },
    border:{
        borderTop: '1px solid black',
        borderBottom: '1px solid black',
        padding: '10px auto',
        textAlign: 'justify',
       // text-ustify: inter-word
    },
    title:{
        fontSize: '20px'
    },
    designer:{
        color: 'grey'
    },
    button:{
        backgroundColor: 'rgb(26, 27, 28)',
        color: 'white',
        fontSize: '15px',
        textTransform: 'uppercase',
        border: '1px solid rgb(26, 27, 28)',
        position: 'relative',
        textAlign: 'center',
        width: '260px',
        height: '30px',
        outline: 'none',
        marginTop: '2em'
    }
}
