import React from 'react';
import axios from 'axios';

export default class AdminCategory extends React.Component{
    state= {
        categories:'', categorie:'', categoriesList:[]
    }

    findCategories = async()=>{
        let url = 'http://localhost:3001/categories';
        try{
            const categories = await axios.get(url);
            this.setState({categoriesList: categories.data.myCategories})
        }
        catch(error){
            debugger
        }
    }
    handleChange = e=>{
        this.setState({[e.target.name]: e.target.value})
    }
    gotoForm = () => {
        this.props.history.push({pathname:'/admin/category/new'})
    }
    handleDelete = () => {

    }
    render(){
    let {categories}=this.state

    //let category = [{category: 'Hats', _id:0}, {category:'Bags', _id:1}, {category:'Belts', _id: 2} ]

        return(
            <div style={decor.big}>
            <div style={{...decor.border, ...decor.title}}>
            <p style={decor.add}>Add a new product</p> 
            <p>chose category or create a new one</p>
            </div>
            <div style={{...decor.grid, ...decor.borderTwo}}>
            <div>
              <div style={decor.select}>
                  {

                  this.state.categoriesList.map(ele=>{
                      return(
                          <div style={decor.alignItems}>
                          <button style={decor.button} className="buttons" onClick={this.gotoForm}>{ele.category}</button>
                          <i className="fas fa-trash" style={decor.icon} onClick={this.handleDelete}></i>
                          <i onClick={()=> this.props.history.push(`/admin/category/${ele._id}`)} //to grab de id of the category you want to update
                          className="fas fa-edit" style={decor.icon}></i>
                          </div>
                      )
                  })     
                }
              </div>  
            </div>
            <div>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new category'
                name= "categories"
                value={categories}
                />       
                <button style={{...decor.button, ...decor.addBtn}} className="buttons" onClick={this.gotoForm}>
                Add Category
                </button> 
            </div>   
            </div>

        </div>
        )
    }
}
const decor={
