import React from 'react';

export default class AdminCategory extends React.Component{
    state= {
        category:''
    }
    handleChange = e=>{
        this.setState({[e.target.name]: e.target.value})
    }
    handleSubmit = () =>{

    }
    render(){
    let {category}=this.state
        return(
            <div style={decor.big}>
            <h2 style={decor.border}>Choose a category to add a new product, or create a new one</h2>
            <div>
                <button style={decor.button}>
                Hats
                </button>                
                <button style={decor.button}>
                Bags
                </button>  
                <button style={decor.button}>
                Belts
                </button>  
            </div>
            <div>
                <label style={decor.label}>Username</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new category'
                name= "category"
                value={category}
                />       
                <button style={decor.button}>
                Login
                </button>  
            </div>   
        </div>
        )
    }
}
const decor={
    big:{
        width: '45%',
        margin: 'auto',
        paddingTop: '5em',
        paddingBottom: '7em'
    },
    border:{
        borderBottom: '2px solid rgb(42, 43, 45)'
    },
    label:{
        marginTop: '1em',
        display: 'flex',
        justifyContent: 'left',
        clear: 'left',
        fontSize: '23px',
        fontFamily:"'EB Garamond', serif",
    },
    select:{
        width:'400px',
        border: '1px solid #cbcbcb',
        height: '24px',
        outline: 'none',
        background: 'white',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif",
    },
    input:{
        width:'360px',
        outline: 'none',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif"
    },
    button:{
        marginTop: '2em',
        marginBotton: '2em',
        width: '200px',
        height:'24px',
        display: 'flex',
        float: 'left',
        fontFamily:"'EB Garamond', serif",
        outline: 'none',
    }
}
