import React from 'react';

export default class Admin extends React.Component{
    state={
        category:'',name:'',description:'', designer:'',style:'', price:'', size:'', color:'', img:'', sale:'false'
    }
    handleChange=e=>{
        this.setState({[e.target.name]: e.target.value})
    }
    handleSubmit=()=>{

    }
    render(){
        let {name,description, designer, style, price, size, color, img, sale}=this.state
        return(
        <div style={decor.big}>
            <h2>Add new product</h2>
            <form  style={decor.box}
            onSubmit={this.handleSubmit}>
                <label style={decor.label} >Category</label>
                <select onChange = {this.handleChange} style={decor.select}>
                    <option name='category' value='shoes'>Shoes</option>
                    <option name='category' value='bags'>Bags</option>
                    <option name='category' value='belts'>Belts</option>
                </select>
                <label style={decor.label} >Product Name</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter product name'
                name='name' 
                value={name}
                />
                <label style={decor.label}>Description</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter description'
                name="description"
                value={description}
                />  
                <label style={decor.label}>Designer</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter designer'
                name="designer"
                value={designer}
                /> 
                <label style={decor.label}>Style</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter style'
                name="style"
                value={style}
                />  
                <label style={decor.label}>Price</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter price'
                name="price"
                value={price}
                />   
                <label style={decor.label}>Size</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter size'
                name="size"
                value={size}
                />        
                <label style={decor.label}>Size</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter color'
                name="color"
                value={color}
                /> 
                <label style={decor.label}> Upload Image</label>         
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='add img url'
                name="img"
                value={img}
                />  
                <label style={decor.label}>Status</label>                
                <select onChange = {this.handleChange} style={decor.select}>
                    <option name='sale' value='true'>on sale</option>
                    <option name='sale' value='false'>regular price</option>
                </select>  
                <div>       
                <button style={decor.button}>
                Submit
                </button>  
                </div>    
            </form>
        </div>
        ) 
    }
}

const decor={
    big:{
        marginTop: '3em',
        marginLeft: 'auto',
        marginRight: 'auto',
        paddingLeft: '2em',
        width: '70%'
    },
    box:{
        border: 'solid 1px black',
    },
    label:{
        marginBottom: '2em',
        display: 'inline-block',
        float: 'left',
        clear: 'left',
        width: '250px',
        textAlign: 'left'
    },
    select:{
        width:'400px',
        border: '1px solid #cbcbcb',
        height: '30px',
        display: 'inline-block',
        float: 'left',
        padding: '6px 30px 6px 6px',
        backgroundPosition: 'right -272px',
        margin: '0'
    },
    input:{
        width:'360px',
        display: 'inline-block',
        float: 'left',
        padding: '6px 30px 6px 6px',
        backgroundPosition: 'right -272px',
        cursor: 'pointer',
        border: '1px solid #cbcbcb',
        borderRadius: '0'
    },
    button:{
        marginTop: '40em',
        width: '200px'

    }
}
