import React from 'react';

export default class Homepage extends React.Component{
    render(){
    return(
        <div>
            <img style={decor.mainpic} src='https://res.cloudinary.com/dyuu7x24r/image/upload/v1549900826/BlanchetteProject/header.webp' alt='mainpicture'/>
            <div style={decor.grid}>
                <img style={decor.mainpic} src='https://res.cloudinary.com/dyuu7x24r/image/upload/v1549903031/BlanchetteProject/hat.jpg' alt='hat'/>
                <img style={decor.mainpic} src='https://res.cloudinary.com/dyuu7x24r/image/upload/v1549902744/BlanchetteProject/bag.jpg' alt='hat'/>
                <img style={decor.mainpic} src='https://res.cloudinary.com/dyuu7x24r/image/upload/v1549902735/BlanchetteProject/belt.jpg' alt='hat'/>
            </div>
            <section style={decor.white}>
                <p style={decor.title}>CHANEL FOR CONCEPTION</p>
                <p style={decor.p}>DISCOVER THE NEW COLLECTION MADE OF A COLLABORATION BETWEEN CONCEPTION AND THE CHANEL. THE NEW SPECIAL COLLECTION IS OFFICIALLY AVAILABLE.</p>
                <button className='buttonswhite' style={decor.button}>Shop now</button>
            </section>
            <div style={decor.column}>
                <div><img style={decor.pic} src='https://res.cloudinary.com/dyuu7x24r/image/upload/v1549911799/BlanchetteProject/chanel2.webp' alt='hat'/></div>
                <div><img style={decor.pic} src='https://res.cloudinary.com/dyuu7x24r/image/upload/v1549912311/BlanchetteProject/Screen_Shot_2019-02-11_at_20.11.18.png' alt='hat'/></div>
            </div>
        </div>
    )
}
}

const decor={
    mainpic:{
        width:'100%',
    },
    grid:{
        width: '95%',
        margin: 'auto',
        display: 'grid',
        gridTemplateColumns: '1fr 1fr 1fr',
        gridGap: '2em',
        marginTop: '2em'
    },
    pic:{
        height: '100%',
        width:'100%'
    },
    column:{
        width: '95%',
        margin: 'auto',
        display: 'grid',
        gridTemplateColumns: '2.11fr 1.02fr',
        gridGap: '2em',
        marginTop: '2em'
    },
    white:{
        height: '400px',
        textAlign: 'center',
        paddingTop: '8%'
    },
    title:{
        fontSize: '30px'
    },
    p:{
        color: 'grey',
        paddingTop: '3em',
        paddingBottom: '3em'
    },
    button:{
        backgroundColor: 'white',
        color: 'black',
        fontSize: '15px',
        textTransform: 'uppercase',
        border: '1px solid rgb(26, 27, 28)',
        position: 'relative',
        textAlign: 'center',
        width: '260px',
        height: '40px',
        outline: 'none',
        marginTop: '2em'
    }
}
