import React from 'react';
import axios from 'axios';

export default class AdminUpdate extends React.Component{
    state={
        newTitle:'', newDesigner:'', newPrice:'', newDescription:'', newCategoryID:'', newColor:'', newImg:'', sale:'', saleDiscount:''
    }
    //sale should be set to false and then we change or empty?
    handleChange=e=>{
        this.setState({[e.target.name]: e.target.value})
    }
    updateProduct=()=>{
        let url = 'http://localhost:3001/products/admin/products/update'
        let { newTitle, newDesigner, newPrice, newDescription, newCategoryID, newColor, newImg}=this.state
        try{
            axios.post(url, {id:this.props.match.params.id, newTitle:newTitle,newDesigner:newDesigner, price:newPrice, description:newDescription, categoryID:newCategoryID, color:newColor, img: newImg})
        }
        catch(error){
            debugger
        }
    }
    render(){
        let { newTitle, newDesigner, newPrice, newDescription, newCategoryID, newColor, newImg, sale, saleDiscount}=this.state
        return(
        <div style={decor.big}>
            <div>
            <h2 style={decor.border}>Update a Product</h2>
            <div style={decor.grid}>
            <div>
                <div>
                <label style={decor.label}>Title</label>
                <p>{ele.title}</p>
                </div>
                <div> 
                <label style={decor.label}>Description</label>
                <p>old description</p>  
                </div> 
                <div>
                <label style={decor.label}>Designer</label>
                <p>old designer</p>
                </div>
                <div>
                <label style={decor.label}>Price</label>
                <p>price</p> 
                </div>
                <div>         
                <label style={decor.label}>Color</label>
                <p>old color</p>
                </div> 
                <div>
                <label style={decor.label}>Upload Image</label>         
                <p>old image</p>
                </div>
                <div>
                <label style={decor.label}>Sale</label>                
                <p>old satus</p> 
                </div>
                <div>
                <label style={decor.label}>Discount</label>                
                <p>old satus</p> 
                </div>
            </div>
            <div>
                <div>
                <label style={decor.label}>Title</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new title'
                name="newTitle"
                value={newTitle}
                />
                </div>
                <div> 
                <label style={decor.label}>Description</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new description'
                name="newDescription"
                value={newDescription}
                />  
                </div> 
                <div>
                <label style={decor.label}>Designer</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new designer'
                name="newDesigner"
                value={newDesigner}
                /> 
                </div>
                <div>
                <label style={decor.label}>Price</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new price'
                name="newPrice"
                value={newPrice}
                />   
                </div>
                <div>         
                <label style={decor.label}>Color</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new color'
                name="newColor"
                value={newColor}
                /> 
                </div> 
                <div>
                <label style={decor.label}>Upload Image</label>         
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='add new img url'
                name="newImg"
                value={newImg}
                />  
                </div>
                <div>
                <label style={decor.label}>Sale</label>                
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='true or false?'
                name="sale"
                value={sale}
                /> 
                </div>                
                <div>         
                <label style={decor.label}>Discount</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new color'
                name="saleDiscount"
                value={saleDiscount}
                /> 
                </div> 
                <div>       
                <button 
                onClick={this.updateProduct}
                style={decor.button}>
                Submit Changes
                </button>  
                </div>    
            </div>
            </div>
            </div>
        </div>
        ) 
    }
}

const decor={
    big:{
        width: '70%',
        margin: 'auto'
    },
    border:{
        borderBottom: '2px solid rgb(42, 43, 45)'
    },
    grid:{
        display: 'grid',
        gridTemplateColumns: '1fr 1fr'
    },
    label:{
        marginTop: '1em',
        display: 'flex',
        justifyContent: 'left',
        clear: 'left',
        fontSize: '23px',
        fontFamily:"'EB Garamond', serif",
    },
    select:{
        width:'400px',
        border: '1px solid #cbcbcb',
        height: '24px',
        outline: 'none',
        background: 'white',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif",
    },
    input:{
        width:'360px',
        outline: 'none',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif"
    },
    button:{
        marginTop: '2em',
        marginBotton: '2em',
        width: '200px',
        height:'24px',
        display: 'flex',
        float: 'left',
        fontFamily:"'EB Garamond', serif",
        outline: 'none'
    }
}
