import React from 'react';
import {NavLink} from 'react-router-dom'

export default class NavBar extends React.Component{
    render(){
        const navbarContent = ['About us','Designers', 'Products', 'Sale']
    return(
        <div style={decor.black} id="top">
            <div style={decor.burguer}>
            <i class="fas fa-bars" style={{color:'white'}}></i>
            </div>
            <div style={{...decor.menu, ...decor.flex}} className='navBarHover'>
                {
                navbarContent.map((ele)=>{
                return <div >{ele}</div>
                })
                }
            </div>
            <div style={decor.search}>
            <input
            style={decor.input}
            />
            <div style={decor.find}>
            <i onClick ={()=>{ return false}}className="fas fa-search" style={{color:'white'}}></i>
            </div>
            </div>
        </div>
    )
    }
}

const decor = {
    burguer:{
        marginLeft:'0.4em',
        marginTop:"0.1em",
        fontSize:'25px'
    },
    black:{
        backgroundColor: 'black',
        display: 'grid',
        gridTemplateColumns: '20% 60% 20%',
        //marginBottom: '10px',
        height: '38px',
        position: 'sticky',
        top: '0',
        zIndex: '1000'
    },
    flex:{
        display: 'flex',
        justifyContent: 'space-evenly'
    },
    menu:{
        display: 'grid',
        gridTemplateColumns: 'repeat(5, 1fr)',
        marginTop:"0.1em",
        color: 'white', 
        fontSize: '20px'
    },
    search:{
        display: 'flex',
        justifyContent: 'flex-end',
        marginRight:'22px'
    },
    find:{
        marginTop: '10px'
    },
    input:{
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        backgroundColor: 'black',
        marginBottom: '6px',
        outline: 'none',
        width: '150px',
        fontSize: '20px',
        color: 'white',
        fontFamily:"'EB Garamond', serif"
    }
}
