import React from 'react';
import axios from 'axios';

export default class Product extends React.Component{
    componentDidMount(){
        this.findProduct()
    }
    findProduct = async () => {
        let url = `http://localhost:3001/products/admin/products/findOne/${this.props.match.params.id}`;
        try{
            const product = await axios.get(url)
            console.log({product})
        }
        catch(error){
            debugger
            console.log({error})
        }
    }
    render(){
        return(
        <div style={decor.small}>
            <div style={decor.grid}>
            <div>
                <img src={[this.props.match.params.id].img} style={decor.img} alt='image'/>
            </div>
            <div style={decor.product}>
                <div style={decor.title}>{[this.props.match.params.id].title}</div>
                <div style={decor.designer}>{[this.props.match.params.id].designer}</div>
                <div>{[this.props.match.params.id].price}</div>
                <div style={decor.border}>{[this.props.match.params.id].description}</div>
                <button onClick={()=> this.props.history.push(`/shoppingcart/${[this.props.match.params.id]._id}`)} style={decor.button} className="buttons">Add</button>
            </div>

        </div>
        </div>
         )
    }
}
const decor = {
    grid:{
        display:'grid',
        gridTemplateColumns: '1fr 1fr'
    },
    small:{
        width: '70%',
        marginLeft: '9em',
    },
    img:{
        paddingTop: '2em',
        Height: '400px',
        width: '80%'
    },
    product:{
        maxHeight: '400px',
        width: '80%',
        paddingTop: '6em',
        fontFamily:"'EB Garamond', serif"
    },
    border:{
        borderTop: '1px solid black',
        borderBottom: '1px solid black',
        padding: '10px auto',
        textAlign: 'justify',
       // text-ustify: inter-word
    },
    title:{
        fontSize: '20px'
    },
    designer:{
        color: 'grey'
    },
    button:{
        backgroundColor: 'rgb(26, 27, 28)',
        color: 'white',
        fontSize: '15px',
        textTransform: 'uppercase',
        border: '1px solid rgb(26, 27, 28)',
        position: 'relative',
        textAlign: 'center',
        width: '260px',
        height: '30px',
        outline: 'none',
        marginTop: '2em'
    }
}
