import React from 'react';
import {NavLink} from 'react-router-dom';



export default class Shoes extends React.Component{
    render(){

    return(
    <div style={decor.small}>
    <div style={decor.shoesDisplay}>
    {
        this.props.stock.map((ele, i)=>{
            return( 
            <div key={i}>
                <img alt="shoe1" style={decor.shoes} src={ele.img}/>
                <p style={decor.name}>{ele.name}</p>
                <p style={decor.brand}>{ele.designer}</p>
                <p style={decor.price}>{ele.price} €</p>
            </div>
            )
        })
    }    
    </div>
    </div>
    )
    }
}
const decor = {
    small: {
        marginRight: '15%',
        marginLeft: '15%'
    },
    shoesDisplay:{
        display: 'grid',
        gridTemplateColumns: 'repeat(3, 1fr)',
        gridGap: '1em'
        // imageHeight: ‘20px’,
        // imageWidth: ‘30px’
    },
    shoes:{
        height: '100%',
        width: '100%'
    }
 }

