import React from 'react';
import axios from 'axios';

export default class AdminUpdate extends React.Component{
    state={
        sale:'' , 
        _id: "",
         title: "", 
         designer: "", 
         price: "", 
         color:'', 
         img:'', 
         description:'',
        newTitle:'', newDesigner:'', newPrice:'', newDescription:'', newCategoryID:'', newColor:'', newImg:'', saleDiscount:''
    }
    componentDidMount(){
        this.findProduct()
    }
    findProduct = async () => {
        debugger
        let url = `http://localhost:3001/products/product/${this.props.match.params.id}`;
        try{
            const product = await axios.get(url)
            debugger
            this.setState({sale:product.data.myProduct.sale , _id:product.data.myProduct._id, title:product.data.myProduct.title, designer:product.data.myProduct.designer, price:product.data.myProduct.price, color:product.data.myProduct.color, img:product.data.myProduct.img, description:product.data.myProduct.description})
            console.log({product})
        }
        catch(error){
            debugger
            console.log({error})
        }
    }
    handleChange=e=>{
        this.setState({[e.target.name]: e.target.value})
    }
    updateProduct=()=>{
        let url = 'http://localhost:3001/products/update'
        let { newTitle, newDesigner, newPrice, newDescription, newCategoryID, newColor, newImg}=this.state
        try{
            axios.post(url, {id:this.props.match.params.id, newTitle:newTitle,newDesigner:newDesigner, price:newPrice, description:newDescription, categoryID:newCategoryID, color:newColor, img: newImg})
        }
        catch(error){
            debugger
        }
    }
    render(){
        let { sale, saleDiscount, title, designer, price, color, img, description, newTitle, newDesigner, newPrice, newDescription,  newColor, newImg}=this.state
        return(
        <div style={decor.big}>
            <div>
            <h2 style={decor.border}>Update a Product</h2>
            <div style={decor.grid}>
            <div>
                <div>
                <label style={decor.label}>Title</label>
                <p>{title}</p>
                </div>
                <div> 
                <label style={decor.label}>Description</label>
                <p>{description}</p>  
                </div> 
                <div>
                <label style={decor.label}>Designer</label>
                <p>{designer}</p>
                </div>
                <div>
                <label style={decor.label}>Price</label>
                <p>{price}</p> 
                </div>
                <div>         
                <label style={decor.label}>Color</label>
                <p>{color}</p>
                </div> 
                <div>
                <label style={decor.label}>Image URL</label>         
                <p>{img}</p>
                </div>
                <div>
                <label style={decor.label}>Sale</label>                
                <p>{sale}</p> 
                </div>
                <div>
                <label style={decor.label}>Discount</label>                
                <p>{saleDiscount}</p> 
                </div>
            </div>
            <div>
                <div>
                <label style={decor.label}>Title</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new title'
                name="newTitle"
                value={newTitle}
                />
                </div>
                <div> 
                <label style={decor.label}>Description</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new description'
                name="newDescription"
                value={newDescription}
                />  
                </div> 
                <div>
                <label style={decor.label}>Designer</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new designer'
                name="newDesigner"
                value={newDesigner}
                /> 
                </div>
                <div>
                <label style={decor.label}>Price</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new price'
                name="newPrice"
                value={newPrice}
                />   
                </div>
                <div>         
                <label style={decor.label}>Color</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new color'
                name="newColor"
                value={newColor}
                /> 
                </div> 
                <div>
                <label style={decor.label}>Upload Image</label>         
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='add new img url'
                name="newImg"
                value={newImg}
                />  
                </div>
                <div>
                <label style={decor.label}>Sale</label>                
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='true or false?'
                name="sale"
                value={sale}
                /> 
                </div>                
                <div>         
                <label style={decor.label}>Discount</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new color'
                name="saleDiscount"
                value={saleDiscount}
                /> 
                </div> 
                <div>       
                <button 
                onClick={this.updateProduct}
                style={decor.button}>
                Submit Changes
                </button>  
                </div>    
            </div>
            </div>
            </div>
        </div>
        ) 
    }
}

const decor={
    big:{
        width: '70%',
        margin: 'auto'
    },
    border:{
        borderBottom: '2px solid rgb(42, 43, 45)'
    },
    grid:{
        display: 'grid',
        gridTemplateColumns: '1fr 1fr'
    },
    label:{
        marginTop: '1em',
        display: 'flex',
        justifyContent: 'left',
        clear: 'left',
        fontSize: '23px',
        fontFamily:"'EB Garamond', serif",
    },
    select:{
        width:'400px',
        border: '1px solid #cbcbcb',
        height: '24px',
        outline: 'none',
        background: 'white',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif",
    },
    input:{
        width:'360px',
        outline: 'none',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif"
    },
    button:{
        marginTop: '2em',
        marginBotton: '2em',
        width: '200px',
        height:'24px',
        display: 'flex',
        float: 'left',
        fontFamily:"'EB Garamond', serif",
        outline: 'none'
    }
}
