export default class OrderMsg extends React.Component {
state = {}
render(){

return(
    <div>
    <p style={decor.box}>
    Thank you for shopping with us!
    You will receive in your e-mail an order confirmation
    </p>
    <button className='buttons' style={decor.button}>Back to Homepage</button>
    </div>
    )
}
}

const decor = {
