import React from 'react';
import {NavLink} from 'react-router-dom'

export default class Shoes extends React.Component{
    render(){

    return(
    <div style={decor.small}>
        <div style={decor.shoesDisplay}>
        {
        this.props.stock.map((ele, i)=>{
            return(
                <NavLink style={decor.navLink} to={'/shoes/:id'}>
                <div key={i}>
                <div><img alt={ele.title} src={ele.image} style={decor.shoes}/></div>       
                <div style={decor.title}>{ele.title}</div>
                <div style={decor.designer}>{ele.designer}</div>
                <div>{ele.price}</div>
                </div>
                </NavLink>
            )
        })
        }
        </div>
    </div>
    )
    }
}
const decor = {
    small: {
        marginRight: '15%',
        marginLeft: '15%'
    },
    shoesDisplay:{
        display: 'grid',
        gridTemplateColumns: 'repeat(3, 1fr)',
        gridGap: '1em'
        // imageHeight: ‘20px’,
        // imageWidth: ‘30px’
    },
    shoes:{
        height: '100%',
        width: '100%'
    },
    navLink:{
        textDecoration: 'none'
    },
    title:{
        position: 'relative',
        textAlign:'center',
        textTransform: 'uppercase'
    },
    designer:{
        position: 'relative',
        textAlign:'center',
        color: 'grey'
    }
 }

