import React from 'react';

export default class AdminLogin extends React.Component{
    status={username:'', password:''}
    render(){
    // handleChange = e=>{
    // this.setState({[e.target.name]: e.target.value})
    // }
    // handleSubmit = () =>{

    // }
        return(
<div style={decor.big}>
            <div>
            <h2 style={decor.border}>Admin Login</h2>
            <div>
                <label style={decor.label}>Username</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter username'
                name="username"
                value={this.state.username}
                />
            </div>
            <div> 
                <label style={decor.label}>Password</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='password'
                name="password"
                value={this.state.password}
                />  
            </div> 
            <div>       
                <button style={decor.button}>
                Login
                </button>  
            </div>   
            </div>
        </div>
        )
    }
}
const decor={
    big:{
        width: '45%',
        margin: 'auto'
    },
    border:{
        borderBottom: '2px solid rgb(42, 43, 45)'
    },
    label:{
        marginTop: '1em',
        display: 'flex',
        justifyContent: 'left',
        clear: 'left',
        fontSize: '23px',
        fontFamily:"'EB Garamond', serif",
    },
    select:{
        width:'400px',
        border: '1px solid #cbcbcb',
        height: '24px',
        outline: 'none',
        background: 'white',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif",
    },
    input:{
        width:'360px',
        outline: 'none',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif"
    },
    button:{
        marginTop: '2em',
        marginBotton: '2em',
        width: '200px',
        height:'24px',
        display: 'flex',
        float: 'left',
        fontFamily:"'EB Garamond', serif",
        outline: 'none'
    }
}
