import React from 'react';

export default class AdminCategory extends React.Component{
    state= {
        category:''
    }
    handleChange = e=>{
        this.setState({[e.target.name]: e.target.value})
    }
    handleSubmit = () =>{

    }

    render(){
    let categories = ['hats', 'bags', 'belts']
    let {category}=this.state
        return(
            <div style={decor.big}>
            <h2 style={decor.border}>Choose a category to add a new product, or create a new one</h2>
            <div style={decor.grid}>
            <div>
            <select>
                {
                    categories.map(ele=>{
                        return <option>{ele}</option>
                    })
                }
            </select> 
                <button className='buttons' style={decor.button}>
                Select Category
                </button>  
            </div>
            <div>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter new category'
                name= "category"
                value={category}
                />       
                <button className='buttons' style={decor.button}>
                Add Category
                </button>  
            </div>   
            </div>
        </div>
        )
    }
}
const decor={
    big:{
        width: '45%',
        margin: 'auto',
        paddingTop: '5em',
        paddingBottom: '7em'
    },
    border:{
        borderBottom: '2px solid rgb(42, 43, 45)'
    },
    grid:{
        display: 'grid',
        gridTemplateColumns: 'repeat(2, 1fr)',
        gridGap: '2em',
        margin: 'auto'
    },
    select:{
        marginTop: '3em',
        width:'200px',
        outline: 'none',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif",
        fontSize: '17px',
    },
    input:{
        marginTop: '3em',
        width:'200px',
        outline: 'none',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif",
        fontSize: '17px',
    },
    button:{
        backgroundColor: 'rgb(26, 27, 28)',
        color: 'white',
        fontSize: '15px',
        textTransform: 'uppercase',
        border: '2px solid rgb(26, 27, 28)',
        position: 'relative',
        textAlign: 'center',
        paddingTop: '15px',
        paddingBottom: '15px',
        paddingRight: '20px',
        paddingLeft: '20px',
        outline: 'none',
    }
}
