import React from 'react';

export default class Admin extends React.Component{
    state={
        title:'', designer:'', price:'', description:'', category:'', color:'', img:'', sale:''
    }
    //sale should be set to false and then we change or empty?
    handleChange=e=>{
        this.setState({[e.target.name]: e.target.value})
    }
    handleSubmit=()=>{

    }
    render(){
        let {title, designer, price, description, category, color, img, sale}=this.state
        return(
        <div style={decor.big}>
            <div>
            <h2 style={decor.border}>Add new product</h2>
            <form onSubmit={this.handleSubmit}>
            <div style={decor.grid}>
            <div>
                <label style={decor.label}>Title</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter title'
                name="title"
                value={title}
                />
            </div>
            <div> 
                <label style={decor.label}>Description</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter description'
                name="description"
                value={description}
                />  
            </div> 
            <div>
                <label style={decor.label}>Designer</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter designer'
                name="designer"
                value={designer}
                /> 
            </div>
            <div>
                <label style={decor.label}>Price</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter price'
                name="price"
                value={price}
                />   
            </div>
            <div>         
                <label style={decor.label}>Color</label>
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='enter color'
                name="color"
                value={color}
                /> 
            </div> 
            <div>
                <label style={decor.label}>Upload Image</label>         
                <input style={decor.input}
                onChange = {this.handleChange}
                placeholder ='add img url'
                name="img"
                value={img}
                />  
            </div>
            <div>
                <label style={decor.label}>Status</label>                
                <select onChange = {this.handleChange} style={decor.select} value={sale}>
                    <option name='sale' value='true'>on sale</option>
                    <option name='sale' value='false'>regular price</option>
                </select>  
            </div>
            <div>       
                <button style={decor.button}>
                Submit
                </button>  
            </div> 
            </div>   
            </form>
            </div>
        </div>
        ) 
    }
}

const decor={
    big:{
        width: '40%',
        margin: 'auto',
        paddingTop: '3em',
        paddingBottom: '4em'
    },
    border:{
        borderBottom: '2px solid rgb(42, 43, 45)'
    },
    grid:{
        display:'grid',
        gridTemplateColumns: '1fr'
    },
    label:{
        marginTop: '1em',
        display: 'flex',
        justifyContent: 'left',
        clear: 'left',
        fontSize: '23px',
        fontFamily:"'EB Garamond', serif",
    },
    select:{
        width:'400px',
        border: '1px solid #cbcbcb',
        height: '24px',
        outline: 'none',
        background: 'white',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif",
    },
    input:{
        width:'300px',
        outline: 'none',
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        fontFamily:"'EB Garamond', serif"
    },
    button:{
        backgroundColor: 'rgb(26, 27, 28)',
        color: 'white',
        fontSize: '15px',
        textTransform: 'uppercase',
        border: '1px solid rgb(26, 27, 28)',
        position: 'relative',
        textAlign: 'center',
        paddingTop: '15px',
        paddingBottom: '15px',
        paddingRight: '20px',
        paddingLeft: '20px',
        outline: 'none',
        marginTop: '10%',
        marginLeft: '36%',
    }
}
