import React from 'react';

export default class Admin extends React.Component{
    state={
        category:'',name:'',description:'', designer:'',style:'', price:'', size:'', color:'', img:'', sale:'false'
    }
    handleChange=e=>{

    }
    handleSubmit=()=>{

    }
    render(){
        let {name,description, designer, style, price, size, color, img, sale}=this.state
        return(
        <div style={decor.big}>
            <h2>Add new product</h2>
            <form onSubmit={this.handleSubmit}>
                <label>Category</label>
                <select onChange = {this.handleChange}>
                    <option name='category' value='shoes'>Shoes</option>
                    <option name='category' value='bags'>Bags</option>
                    <option name='category' value='belts'>Belts</option>
                </select>
                <label>Product Name</label>
                <input style={{width:'400px'}}
                onChange = {this.handleChange}
                placeholder ='enter product name'
                name='name' 
                value={name}
                />
                <label>Description</label>
                <input
                onChange = {this.handleChange}
                placeholder ='enter description'
                name="description"
                value={description}
                />
                <label>Designer</label>
                <input
                onChange = {this.handleChange}
                placeholder ='enter designer'
                name="designer"
                value={designer}
                />
                <label>Style</label>
                <input
                onChange = {this.handleChange}
                placeholder ='enter style'
                name="style"
                value={style}
                />
                <label>Price</label>
                <input
                onChange = {this.handleChange}
                placeholder ='enter price'
                name="price"
                value={price}
                />
                <label>Size</label>
                <input
                onChange = {this.handleChange}
                placeholder ='enter size'
                name="size"
                value={size}
                />       
                <label>Size</label>
                <input
                onChange = {this.handleChange}
                placeholder ='enter color'
                name="color"
                value={color}
                />       >
                <label>Image</label>         
                <input
                onChange = {this.handleChange}
                placeholder ='add img'
                name="img"
                value={img}
                />
                <label>Status</label>                
                <select onChange = {this.handleChange}>
                    <option name='sale' value='true'>on sale</option>
                    <option name='sale' value='false'>regular price</option>
                </select>
                <button>Submit</button>
            </form>
        </div>
        ) 
    }
}

const decor={
    big:{
        border: 'solid 1px black',
        marginTop: '3em',
        marginLeft: 'auto',
        marginRight: 'auto',
        paddingLeft: '2em',
        width: '70%'
    }

}
