import React from 'react';
import {NavLink} from 'react-router-dom';

export default class SideBar extends React.Component{
    state ={
        display:'block'
    }
    hideSideBar = () =>{
        this.setState({display:'none'})
    }
    render(){
    let categories =['hats', 'bags', 'belts']
        return(
            <div style={{display:this.props.display, width:'200px'}}>
                <p style={decor.title}>
                    <i 
                    onClick={()=>this.props.hideSideBar()}
                    style={decor.icon} className="fas fa-arrow-left">
                    </i>
                    Filter By
                </p>
                <div>
                {
                categories.map(ele=>{
                    return( <NavLink to={`/products/${ele}`}> <p className='sidebarItem' style={decor.item}>{ele}</p></NavLink>
                    )
                })
                }
                </div>
            </div>
        )
    }
}

const decor={
    main:{
        width: '200px',
        marginRight: '0%'
    },
    title:{
        borderBottom: '2px solid rgb(42, 43, 45)',
        fontSize: '20px',
        textAlign: 'right',
        textTransform: 'uppercase',
        margin: '1em auto',
        paddingTop: '1em'

    },
    item:{
        borderBottom:'1px solid rgb(242, 242, 242)',
        textAlign: 'right',
        textTransform: 'uppercase', 
        margin: '2em auto', 
        paddingBottom: '0.4em',
        textDecoration: 'none'
    },
    icon:{
        fontSize: '15px',
         color: 'grey',
         paddingRight: '40%'
    }
}
