import React from 'react';
import {NavLink} from 'react-router-dom';
import axios from 'axios';

export default class SideBar extends React.Component{
    state ={
        display:'block', categoriesList:[]
    }
    componentDidMount(){
        this.findCategories();
      }

    findCategories = async()=>{
        let url = 'http://localhost:3001/categories/admin';
        try{
            const categories = await axios.get(url);
            this.setState({categoriesList: categories.data.myCategories})
            console.log(this.state.categoriesList, categories)
        }
        catch(error){
            console.log(error)
            debugger
        }
    }
    handleMenu =(banana)=>{
        this.props.history.push({pathname:`/products/products_by_category/${ele._id}`})
        this.props.findProductsByCategory(banana);
    }

    render(){

        return(
            <div style={{display:this.props.display, width:'90%'}}>
                <p style={decor.title}>
                    {/* <i 
                    onClick={()=>this.props.hideSideBar()}
                    style={decor.icon} className="fas fa-arrow-left">
                    </i> */}
                    Categories
                </p>
                <div>
                {
                this.state.categoriesList.map(ele=>{
                    return( 
                    //<NavLink to={`/products/products_by_category/${ele._id}`}> 
                    <p onClick={()=>this.handleMenu(ele._id) } className='sidebarItem' style={decor.item}>{ele.category}</p>
                    //</NavLink>
                    )
                })
                }
                </div>
                <p style={decor.title}>Designer</p>
                <p style={decor.title}>Size</p>
                <p style={decor.title}>Color</p>
            </div>
        )
    }
}

const decor={
    main:{
        width: '100%'
    },
    title:{
        borderBottom: '2px solid rgb(42, 43, 45)',
        fontSize: '20px',
        textAlign: 'right',
        textTransform: 'uppercase',
        margin: '1em auto',
        paddingTop: '1em'

    },
    item:{
        borderBottom:'1px solid rgb(242, 242, 242)',
        textAlign: 'right',
        textTransform: 'uppercase', 
        margin: '2em auto', 
        paddingBottom: '0.4em',
        textDecoration: 'none'
    },
    icon:{
        fontSize: '15px',
         color: 'grey',
         paddingRight: '40%'
    }
}
