import React from 'react';
//import {NavLink} from 'react-router-dom'

export default class AdminDeleteUpdate extends React.Component{
    render(){
         return(
             <div style={decor.small}>
                 <div style={decor.bagsDisplay}>
                 {
                     this.props.stock.map ((ele, i) => { //the props come from myrouter.js
                         return(
                             <div key={i}>
                             <div className='imgHover'><img src={ele.image} style={decor.bags} alt={ele.title}/></div>
                             <div style={decor.title}>{ele.title}</div>
                             <div style={decor.designer}>{ele.designer}</div>
                             <div style={decor.price}>{ele.price}</div>
                             <div style={decor.position}>
                             <button style={decor.button}>Delete</button>
                             <button style={decor.button}>Update</button>
                             </div>
                             </div>
                         )
                     })
                 }
                 </div>
             </div>
        )
     }
 }

 const decor = {
     small: {
         marginRight: '15%',
         marginLeft: '15%',
         marginTop: '3em'
     },
     bagsDisplay:{
        display: 'grid',
        gridTemplateColumns: 'repeat(3, 1fr)',
        gridGap: '1em',

         // imageHeight: '20px',
         // imageWidth: '30px'
     },
     bags:{
         height: '100%',
         width: '100%',
     },
     title:{
         textTransform: 'uppercase',
         position: 'relative',
         textAlign: 'center'
     },
     designer: {
         color: 'rgb(187, 192, 201)',
         position: 'relative',
         textAlign: 'center'
     },
     price:{
         position: 'relative',
         textAlign: 'center'
     },
     position:{
        position: 'relative',
        textAlign: 'center'
     }
 }
