import React from 'react';

export default class Bag extends React.Component{
    state= {title: 'GG Blooms Dionysus Wallet Bag', designer:'Gucci', price: '€960', description: 'nspired by the Greek god Dionysus - said to have crossed the river Tigris on a tiger sent by Zeus - this Gucci bag is an investment in timeless Italian style.', category: 'bags', color: 'brown', image: 'https://images.harrods.com/product/gucci/gg-blooms-dionysus-wallet-bag_000000000005269686.jpg', sale: false}
    render(){
         return(
        <div style={decor.grid}>
            <img alt='image' src={this.state.image}/>
            <div>{this.state.title}</div>
        </div>
         )
    }
}
const decor = {
    grid:{
        display:'grid',
        gridTemplateColumns: '1fr 1fr'
    }
}
