import React from 'react';
import {NavLink} from 'react-router-dom'

export default class NavBar extends React.Component{
    state = {
        input:''
    }
    handleChange=e=>{
        this.setState({input:e.target.value})
    }
    search=()=>{
    return false
    }

    render(){
        const navbarContent = ['About us','Designers', 'Products', 'Sale']
    return(
        <div style={decor.black} id="top">
            <div style={decor.burguer}>
            <NavLink to='/products'><i class="fas fa-bars" style={{color:'white'}}></i></NavLink>
            </div>
            <div style={{...decor.menu, ...decor.flex}} className='navBar'>
                {
                navbarContent.map((ele)=>{
                return <NavLink to={`/${ele}`}> <div >{ele}</div> </NavLink>
                })
                }
            </div>
            <div style={decor.search}>
            <input
            onChange={this.handleChange}
            style={decor.input}
            />
            <div style={decor.find}>
            <i onClick={this.search} className="fas fa-search" style={{color:'white'}}></i>
            </div>
            </div>
        </div>
    )
    }
}

const decor = {
    burguer:{
        marginLeft:'0.4em',
        marginTop:"0.1em",
        fontSize:'25px'
    },
    black:{
        backgroundColor: 'black',
        display: 'grid',
        gridTemplateColumns: '20% 60% 20%',
        height: '38px',
        position: 'sticky',
        top: '0',
        zIndex: '1000'
    },
    flex:{
        display: 'flex',
        justifyContent: 'space-evenly'
    },
    menu:{
        display: 'grid',
        gridTemplateColumns: 'repeat(5, 1fr)',
        marginTop:"0.1em",
        color: 'white', 
        fontSize: '20px'
    },
    search:{
        display: 'flex',
        justifyContent: 'flex-end',
        marginRight:'22px'
    },
    find:{
        marginTop: '10px'
    },
    input:{
        borderTop:'none',
        borderRight:'none',
        borderLeft: 'none',
        backgroundColor: 'black',
        marginBottom: '6px',
        outline: 'none',
        width: '150px',
        fontSize: '20px',
        color: 'white',
        fontFamily:"'EB Garamond', serif"
    }
}
