import React from 'react';

export default class SideBar extends React.Component{

    render(){
    let categories =['hats', 'bags', 'belts']
        return(
            <div style={decor.main}>
                <p style={decor.title}>Filter By</p>
                <div>
                {
                categories.map(ele=>{
                    return <p className='sidebarItem' style={decor.item}>{ele}</p>
                })
                }
                </div>
            </div>
        )
    }
}

const decor={
    main:{
        width: '12%'
    },
    title:{
        borderBottom: '2px solid rgb(42, 43, 45)',
        fontSize: '20px',
        textAlign: 'center',
        textTransform: 'uppercase',
        marginBottom: '0%'

    },
    item:{
        borderBottom:'1px solid rgb(242, 242, 242)',
        textAlign: 'right',
        textTransform: 'uppercase', 
        margin: '2em auto', 
        paddingBottom: '0.4em'
    }
}
